        Job(int startTime, int endTime, int profit) {
            this.startTime = startTime;
            this.endTime = endTime;
            this.profit = profit;
        }
    }
    public int jobScheduling(int[] startTime, int[] endTime, int[] profit) {
        List<Job> jobs = new ArrayList<>();
        for(int i=0; i<startTime.length; i++) {
            jobs.add(new Job(startTime[i], endTime[i], profit[i]));
        }

        Collections.sort(jobs, (a,b) -> a.endTime - b.endTime);
        TreeMap<Integer, Integer> map = new TreeMap<>();
        int ans = 0;

        for(Job currJob : jobs) {
            Integer entryTillStartTime = map.floorKey(currJob.startTime);
            int maxProfitStartTime = entryTillStartTime==null ? 0 : map.get(entryTillStartTime);
            ans = Math.max(ans, maxProfitStartTime + currJob.profit);
            map.put(currJob.endTime, ans);
        }
        return ans;
    }
}

public class Solution {
    public int JobScheduling(int[] startTime, int[] endTime, int[] profit) {
        int n = startTime.Length;
        List<Job> jobs = new List<Job>();

        for (int i = 0; i < n; i++) {
            jobs.Add(new Job(startTime[i], endTime[i], profit[i]));
        }

        jobs.Sort((x, y) => x.EndTime.CompareTo(y.EndTime));

        int[] dp = new int[n + 1];

        for (int i = 1; i <= n; i++) {
            dp[i] = Math.Max(dp[i - 1], jobs[i - 1].Profit);
            for (int j = i - 1; j > 0; j--) {
                if (jobs[i - 1].StartTime >= jobs[j - 1].EndTime) {
                    dp[i] = Math.Max(dp[i], dp[j] + jobs[i - 1].Profit);
                    break;
                }
            }
        }

        return dp[n];
    }
