class Solution {
private:
    static bool mycmp(jobState &j1 , jobState &j2){
        return j1.start <= j2.start;
    }

    int getNextIndex(vector<jobState>&info,int currEnd){
        int index = -1;
        int start = -1,end = info.size();
        while(end - start > 1){
            int mid = start + (end-start)/2;
            if(info[mid].start >= currEnd){
                index = mid;
                end = mid;
            }
            else{
                start = mid;
            }
        }
        return index;
    }
    int f(int idx, vector<jobState>&info){
        if(idx >= info.size()){
            return 0;
        }
        if(dp[idx]!=-1) return dp[idx];
        int currEnd = info[idx].end;
        int nextIdx = getNextIndex(info,currEnd);
        int take = info[idx].profit + f(nextIdx,info);
        int notTake = f(idx+1,info);
        return dp[idx] = max(take,notTake);
    }
public:
    int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {
        memset(dp,-1,sizeof(dp));
        int n = startTime.size();
        vector<jobState>vp(n);
        for(int i = 0 ; i < n ; i++){
            vp[i].start = startTime[i];
            vp[i].end = endTime[i];
            vp[i].profit = profit[i];
        }
        sort(vp.begin(),vp.end(),mycmp);
        int ans = f(0,vp);
        return ans;
    }
};

class Solution {
    // a novel method for solving this partiular problem. does it in O(nlogn) time, O(N) space.
    public int jobScheduling(int[] startTime, int[] endTime, int[] profit) {
        // create easier-to-sort 2d array holding all jobs. O(N) time, O(N) space where N is the number of jobs.
        int[][] jobs = new int[startTime.length][];
        for (int i = 0; i < jobs.length; i++) {
            jobs[i] = new int[]{startTime[i], endTime[i], profit[i]};
        }
        // sort the array depending on the start times in ascending order. O(nlogn) time.
        Arrays.sort(jobs, (a, b) -> a[0] - b[0]);
        // create a priority queue which holds possible later-bests (time-profit pairs which denote a possible profit available at a time)
        PriorityQueue<int[]> laterBests = new PriorityQueue<>((a, b) -> a[0] - b[0]);
        // the best profit available before or at the current time. (starts at 0, continues with curTime)
        int max = 0;
        for (int[] job : jobs) {
            // iterates through all jobs. sets the curTime to the start time of the current job.
            int curTime = job[0];
            while (!laterBests.isEmpty()) {
                // resolves the invariant. updates max to be equal to the best of all time-profit pairs in laterBests which have a time less than or equal to curTime.
                int[] possible = laterBests.poll();
                if (possible[0] > curTime) {
                    // breaks when a time too large is found.
                    laterBests.offer(possible);
                    break;
                }
                if (possible[1] > max) max = possible[1];
            }
            // adds a new entry, with the time endTime (of the current job) and a profit equal to the max thus far added to the profit of the current job.
            laterBests.add(new int[]{job[1], max + job[2]});
        }
        // cleans up the priority queue by mopping up any better profits available at times later than the latest job start time. these, too, are available.
        while (!laterBests.isEmpty()) {
            int[] possible = laterBests.poll();
            if (possible[1] > max) max = possible[1];
        }
        // returns the maximum time.
        return max;
    }
}

class Solution {
    private int binarySearch(int[][] jobs, int end, int left, int right) {
        int result = -1;

        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (jobs[mid][1] <= end) {
                result = mid;
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }

        return result;
    }

    public int jobScheduling(int[] startTime, int[] endTime, int[] profit) {
        int n = startTime.length;
        int[][] jobs = new int[n][3];

        for (int i = 0; i < n; i++) {
            jobs[i][0] = startTime[i];
            jobs[i][1] = endTime[i];
            jobs[i][2] = profit[i];
        }

        Arrays.sort(jobs, Comparator.comparingInt(m -> m[1]));

        int[] dp = new int[n];
        dp[0] = jobs[0][2];

        for (int i = 1; i < n; i++) {
            int prev = 0;
            int lastJobIndex = binarySearch(jobs, jobs[i][0], 0, i - 1);

            if (lastJobIndex != -1) {
                prev = dp[lastJobIndex];
            }

            dp[i] = Math.max(prev + jobs[i][2], dp[i - 1]);
        }

        return dp[n - 1];
    }
}

class Solution {
    public int jobScheduling(int[] startTime, int[] endTime, int[] profit) {
        int len = startTime.length;
        int[][] dp = new int[len][3];
        for(int i=0; i<len; i++) {
            dp[i] = new int[]{startTime[i], endTime[i], profit[i]};
        }
        Arrays.sort(dp, (a,b) -> a[1]-b[1]);
        TreeMap<Integer, Integer> intsMap = new TreeMap<>();
        intsMap.put(0,0);
        for(var i : dp) {
            int cur = intsMap.floorEntry(i[0]).getValue() + i[2];
            if(cur > intsMap.lastEntry().getValue()) {
                intsMap.put(i[1], cur);
            }
        }
        return intsMap.lastEntry().getValue();
    }
}

public class Solution {
    private Meeting[] _meetings;
    private int[] _memoization;

    private const int EmptyState = -1;

    public int JobScheduling(int[] startTime, int[] endTime, int[] profit)
    {
        Initialization(startTime, endTime, profit);
        return FindMaxProfit(0);
    }

    private int FindMaxProfit(int index)
    {
        if (index >= _meetings.Length)
        {
            return 0;
        }

        if (_memoization[index] != EmptyState)
        {
            return _memoization[index];
        }

        var nextMeetingIndex = FindNextAvailableMeeting(index + 1, _meetings[index].EndTime);

        var skip = FindMaxProfit(index + 1);
        var schedule = _meetings[index].Profit + FindMaxProfit(nextMeetingIndex);

        return _memoization[index] = Math.Max(skip, schedule);
    }

    int FindNextAvailableMeeting(int currentIndex, int nextStartTime)
    {
        var start = currentIndex;
        var end = _meetings.Length - 1; 
        var nextAvailableMeetingIndex = _meetings.Length;

        while (start <= end)
        {
            var mid = (start + end) / 2;
            if (_meetings[mid].StartTime >= nextStartTime)
            {
                nextAvailableMeetingIndex = mid;
                end = mid - 1;
            }
            else
            {
                start = mid + 1;
            }
        }

        return nextAvailableMeetingIndex;
    }

    void Initialization(int[] startTime, int[] endTime, int[] profit)
    {
        _meetings = new Meeting[startTime.Length];
        _memoization = new int[startTime.Length + 1];

        for (int i = 0; i < startTime.Length; i++)
        {
            _meetings[i] = new Meeting(startTime[i], endTime[i], profit[i]);
        }

        Array.Fill(_memoization, EmptyState);
        Array.Sort(_meetings);
    }

    record Meeting(int StartTime, int EndTime, int Profit) : IComparable<Meeting>
    {
        public int CompareTo(Meeting? other)
        {
            return StartTime.CompareTo(other.StartTime);
        }
    }
}

class Solution {
    public int jobScheduling(int[] startTime, int[] endTime, int[] profit) {
        int n = startTime.length;
        int[][] jobs = new int[n][3];

        for (int i = 0; i < n; i++) {
            jobs[i] = new int[]{startTime[i], endTime[i], profit[i]};
        }

        Arrays.sort(jobs, Comparator.comparingInt(a -> a[1]));

        TreeMap<Integer, Integer> dp = new TreeMap<>();
        dp.put(0, 0);

        for (int[] job : jobs) {
            int prevProfit = dp.floorEntry(job[0]).getValue();
            int maxProfit = prevProfit + job[2];

            if (maxProfit > dp.lastEntry().getValue()) {
                dp.put(job[1], maxProfit);
            }
        }

