class Solution {
    public int jobScheduling(int[] startTime, int[] endTime, int[] profit) {
        int n=startTime.length;
        Job jobs[]=new Job[n];
        for(int i=0; i<n; i++) {
            jobs[i]=new Job(startTime[i], endTime[i], profit[i]);
        }
        Arrays.sort(jobs, (j1, j2)->{
            if(j1.start!=j2.start) { // sort by startTime first
                return j1.start-j2.start;
            }else if(j1.end!=j2.end) { // then by end time
                return j1.end-j2.end;
            }else { //then by profit
                return j2.profit-j1.profit;
            }
        });
        dp=new Integer[n];
        return util(jobs, 0);
    }

    private Integer dp[];

    private int util(Job jobs[], int i) {
        if(i==jobs.length) {
            return 0;
        }

        if(dp[i]!=null) return dp[i];

        int ret=0;
        //schedule this jobs[i]
        //binary search
        int l=i;
        int h=jobs.length;
        while(l+1<h) {
            int mid=(l+h)/2;
            if(jobs[mid].start>=jobs[i].end) {
                h=mid;
            }else {
                l=mid;
            }
        }
        ret=Math.max(ret, jobs[i].profit+util(jobs, h));
        //don't schedule
        ret=Math.max(ret, util(jobs, i+1));
        return dp[i]=ret;
    }

    private class Job {
        public int start;
        public int end;
        public int profit;
        public Job(int s, int e, int p) {
            this.start=s;
            this.end=e;
            this.profit=p;
        }
    }
}

