class Solution {
  public int jobScheduling(int[] startTime, int[] endTime, int[] profit) {
    final int n = startTime.length;
    dp = new int[n + 1];
    Job[] jobs = new Job[n];

    for (int i = 0; i < n; ++i)
      jobs[i] = new Job(startTime[i], endTime[i], profit[i]);

    Arrays.sort(jobs, (a, b) -> a.startTime - b.startTime);
    for (int i = 0; i < n; ++i)
      startTime[i] = jobs[i].startTime;

    return jobScheduling(jobs, startTime, 0);
  }

  private int[] dp;

  private int jobScheduling(Job[] jobs, int[] startTime, int i) {
    if (i == jobs.length)
      return 0;
    if (dp[i] > 0)
      return dp[i];

    final int j = firstGreaterEqual(startTime, i + 1, jobs[i].endTime);
    final int pick = jobs[i].profit + jobScheduling(jobs, startTime, j);
    final int skip = jobScheduling(jobs, startTime, i + 1);
    return dp[i] = Math.max(pick, skip);
  }

  private int firstGreaterEqual(int[] A, int startFrom, int target) {
    int l = startFrom;
    int r = A.length;
    while (l < r) {
      final int m = (l + r) / 2;
      if (A[m] >= target)
        r = m;
      else
        l = m + 1;
    }
    return l;
  }
}

class Solution {
    private record Jobs(int startTime,int endTime, int profit){}
    public int jobScheduling(int[] startTime, int[] endTime, int[] profit) {

        int noOfJobs = startTime.length;
        var jobs = new Jobs[noOfJobs];

        for(int i=0; i < noOfJobs; i++){
            jobs[i] = new Jobs(startTime[i], endTime[i], profit[i]);
        }

        Arrays.sort(jobs, Comparator.comparingInt(job-> job.endTime));
        var dp = new int[noOfJobs+1];

        for(int i= 0 ; i < noOfJobs ;i++ ){

            int prevJob = getPreviousNonOverLappingJob(jobs,i,jobs[i].startTime);
            dp[i+1] = Math.max(dp[i], dp[prevJob] + jobs[i].profit );
        }

        return dp[noOfJobs];
    }

    private static int getPreviousNonOverLappingJob(Jobs[] jobs,int endIndex, int targetTime){
        int low = 0;
        int high = endIndex;

        while(low < high){
            int mid = (low + high)/2;
            if(jobs[mid].endTime <= targetTime){
                low = mid + 1;
            }else {
                high = mid;
            }
        }
        return low;
    }
}

