import java.util.Arrays;
import java.util.TreeMap;

class Solution {
    public int jobScheduling(int[] startTime, int[] endTime, int[] profit) {
        int n = startTime.length;

        // Create an array to store jobs
        Job[] jobs = new Job[n];
        for (int i = 0; i < n; i++) {
            jobs[i] = new Job(startTime[i], endTime[i], profit[i]);
        }

        // Sort jobs based on end time
        Arrays.sort(jobs, (a, b) -> Integer.compare(a.end, b.end));

        // TreeMap to store maximum profit at each time point
        TreeMap<Integer, Integer> dp = new TreeMap<>();
        dp.put(0, 0); // Initial profit is 0

        for (Job job : jobs) {
            // Find the closest previous job that doesn't overlap
            Integer prevJobEnd = dp.floorKey(job.start);

            // Calculate the new profit if we include the current job
            int newProfit = dp.get(prevJobEnd) + job.profit;

            // Update the maximum profit at the current time
            int maxProfit = Math.max(newProfit, dp.lastEntry().getValue());
            dp.put(job.end, maxProfit);
        }

        return dp.lastEntry().getValue();
    }
}

