class Solution {
    private int findLatestNonOverlap(int[][] jobs, int currentJob) {
        for (int i = currentJob - 1; i >= 0; i--) {
            if (jobs[i][1] <= jobs[currentJob][0]) {
                return i;
            }
        }
        return -1;
    }
    public int jobScheduling(int[] startTime, int[] endTime, int[] profit) {
        int n = startTime.length;
        int[][] jobs = new int[n][3];

        for (int i = 0; i < n; i++) {
            jobs[i][0] = startTime[i];
            jobs[i][1] = endTime[i];
            jobs[i][2] = profit[i];
        }

        Arrays.sort(jobs, Comparator.comparingInt(o -> o[1]));

        int[] dp = new int[n];
        dp[0] = jobs[0][2];

        for (int i = 1; i < n; i++) {
            int currentProfit = jobs[i][2];
            int latestNonOverlap = findLatestNonOverlap(jobs, i);

            if (latestNonOverlap != -1) {
                currentProfit += dp[latestNonOverlap];
            }

            dp[i] = Math.max(currentProfit, dp[i - 1]);
        }

        return dp[n - 1];
    }
}

class Solution {
public:

    int jobScheduling(vector<int>& s, vector<int>& e, vector<int>& p) {

        int n = s.size(); 
        vector<pair<int, int>> st(n);
        for(int i = 0; i < n; i++) st[i] = {s[i], i};
        sort(st.begin(), st.end());
        vector<int> dp(n+1, 0); 
        for(int i = n-1; i > -1; i--)
            dp[i] += max(dp[i+1], p[st[i].second] + dp[lower_bound(st.begin() + i, st.end(), make_pair(e[st[i].second], 0)) - st.begin()]);
        return dp[0];
    }
};

class Solution {
public:
    int rec( int idx , vector<vector<int>>&jobs , vector<int>&startTime , vector<int>&dp)
    {
        if( idx >= startTime.size())
        {
            return 0;
        }

        if(dp[idx] != -1)
        {
            return dp[idx];
        }

        int nextIndex = lower_bound(startTime.begin() , startTime.end() , jobs[idx][1])-startTime.begin();

        int take = jobs[idx][2] + rec( nextIndex , jobs , startTime , dp);

        int notTake = rec( idx + 1 , jobs , startTime , dp);

        return dp[idx] = max( take , notTake);
    }
    int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {

         vector<vector<int>>jobs;

         int n = startTime.size();

         vector<int>dp( n , -1);

         for( int i = 0 ; i < startTime.size() ; i++)
         {
             jobs.push_back({ startTime[i] , endTime[i] , profit[i]});
         }

         sort(jobs.begin() , jobs.end());

         sort(startTime.begin() , startTime.end());

         return rec( 0 , jobs , startTime , dp);
    }
};

class Solution {
public:
    // time/space: O(nlogn)/O(n)
    int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {
        // sort the job {end, start, profit} by the end time
        const int n = startTime.size();
        vector<vector<int>> jobs(n);
        for (int i = 0; i < n; i++) jobs[i] = {endTime[i], startTime[i], profit[i]};
        sort(jobs.begin(), jobs.end());

        // dynamic programming {end, profit}
        map<int, int> dp = {{0, 0}};
        for (auto& job : jobs) {
            // take the current job based on the right-most non-overlapping job
            // the end time of the selected job must be smaller or equal to the start time of the current job
            // so we can use `prev(upper_bound())` to meet the condition `currStart >= prevEnd`
            int profit = prev(dp.upper_bound(job[1]))->second + job[2];
            // append the element if it's best so far
            if (profit > dp.rbegin()->second) dp[job[0]] = profit;
        }
        return dp.rbegin()->second;
    }
};

class Solution {
public:

    int find(int i, vector<vector<int>>&job, vector<int>&startTime, int n,vector<int>&dp){
        if(i>=n)return 0;
        if(dp[i]!=-1)return dp[i];

        int index = lower_bound(startTime.begin(),startTime.end(),job[i][1])-startTime.begin();
        int pick = job[i][2] + find(index,job,startTime,n,dp);
        int notpick = find(i+1,job,startTime,n,dp);
        return dp[i] = max(pick,notpick);

    }

