class Solution {
public:
    int numJobs;
    int memo[50001];


    int getNextIndex(vector<vector<int>>&jobs , int l , int end){
        int r = numJobs - 1;
        int res = numJobs;
        while(l <= r){
            int mid = l + ((r-l) >> 1);

            if(jobs[mid][0] >= end){
                res = mid;
                r = mid - 1;
            }
            else{
                l = mid + 1;
            }
        }
        return res;
    }
    int solve(vector<vector<int>>&jobs ,vector<int>&startTime , int i){

        if(i >= numJobs){
            return 0;
        }


        //if we have already calculated the max profit of that job
        if(memo[i] != -1){
            return memo[i];
        }

        //include
        int next = getNextIndex(jobs , i+1 , jobs[i][1]);
        //we are using binary search here to get the next index
        //it helps us to find the lower bound of the next safe job to take
        //so passing jobs , start , end

        //instead we can also use lower_bound function directly but it is always better to write your own function

        // int next = lower_bound(startTime.begin() , startTime.end() , jobs[i][1]) - startTime.begin();

        int inc = jobs[i][2] + solve(jobs , startTime , next);


        //not include
        int not_inc = solve(jobs , startTime, i + 1);

        return memo[i] = max(inc , not_inc);
    }
    int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {
        numJobs = startTime.size();
        memset(memo , -1 , sizeof(memo));

        vector<vector<int>>jobs(numJobs , vector<int>(3,0));
        for(int i = 0 ; i < numJobs ; i++){
            jobs[i][0] = startTime[i];
            jobs[i][1] = endTime[i];
            jobs[i][2] = profit[i];
        }

        //we can also create jobs in a diff way
        // vector<tuple<int,int,int>>jobs(numJobs);

        // for(int i = 0 ; i < numJobs ;i++){
        //     jobs[i] = {startTime[i] , endTime[i] , profit[i]};
        // }

        //by default -> sorted on the basis of first value but still writing comp for better undertsanding
        auto comp = [&](auto& vec1 , auto& vec2){
            return vec1[0] <= vec2[0];
        };

        //sort it on the basis of start time
        sort(jobs.begin() , jobs.end() , comp);

        return solve(jobs , startTime ,0);
    }
};

class Solution {
public:
    int solve(int i, vector<int>& startTime, vector<int>& endTime, vector<int>& profit, vector<int> &dp){
        if(i == startTime.size()) return 0;
        if(dp[i] != -1) return dp[i];

        int ind = lower_bound(startTime.begin(), startTime.end(), endTime[i]) - startTime.begin();
        int take = profit[i] + solve(ind, startTime, endTime, profit, dp);
        int notTake = solve(i+1, startTime, endTime, profit, dp);

        return dp[i] = max(take, notTake);
    }

    int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {
        int n = startTime.size();

        set<pair<int, pair<int, int>>> st;
        for(int i=0; i<startTime.size(); i++){
            st.insert({startTime[i], {endTime[i], profit[i]}});
        }

        int i = 0;
        for(auto it: st){
            pair<int, pair<int, int>> p = it;
            startTime[i] = p.first;
            endTime[i] = p.second.first;
            profit[i] = p.second.second;

            i = i + 1;
        }


        vector<int> dp(n+1, -1);
        return solve(0, startTime, endTime, profit, dp);
    }
};

class Solution {
public:
   int binarySearch(vector<pair<int, pair<int, int>>>& jobs, int index) {
    int low = 0, high = index - 1;

    while (low <= high) {
        int mid = (low + high) / 2;

        if (jobs[mid].first <= jobs[index].second.first) {
            if (jobs[mid + 1].first <= jobs[index].second.first) {
                low = mid + 1;
            } else {
                return mid;
            }
        } else {
            high = mid - 1;
        }
    }

    return -1;
}

int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {
    int n = startTime.size();
    vector<pair<int, pair<int, int>>> jobs;

    for (int i = 0; i < n; i++) {
        jobs.push_back({endTime[i], {startTime[i], profit[i]}});
    }

    // Sort jobs based on their end times
    sort(jobs.begin(), jobs.end());

    // dp[i] represents the maximum profit we can get up to the ith job
    vector<int> dp(n, 0);
    dp[0] = jobs[0].second.second;

    for (int i = 1; i < n; i++) {
        int currentProfit = jobs[i].second.second;

        // Find the latest job that finishes before the current job starts using binary search
        int prevCompatibleJobIndex = binarySearch(jobs, i);

        if (prevCompatibleJobIndex != -1) {
            currentProfit += dp[prevCompatibleJobIndex];
        }

        // Update the maximum profit up to the current job
        dp[i] = max(dp[i - 1], currentProfit);
    }

    return dp[n - 1];
}
};

class Solution {
    public int Solve(int arr[][],int index,int dp[])
    {
        if(index==arr.length)
        {
            return 0;
        }
        // 1st case take the value
        if(dp[index]!=0)
        {
            return dp[index];
        }
        int end=arr[index][1];
        int low=0,high=arr.length-1,i=0;
        while(low<=high)
        {
            int mid=(low+high)/2;
            if(arr[mid][0]>=end)
            {
                i=mid;
                high=mid-1;
            }
            else
            {
                i=mid+1;
                low=mid+1;
            }
        }

        int op1=arr[index][2]+Solve(arr,i,dp);
        // 2nd case, don't take the value
        int op2=0+Solve(arr,index+1,dp);

        return dp[index]=Math.max(op1,op2);
    }
    public int jobScheduling(int[] startTime, int[] endTime, int[] profit) {
        int dp[]=new int[startTime.length];
        int arr[][]=new int[startTime.length][3];
        for(int i=0;i<arr.length;i++)
        {
            arr[i][0]=startTime[i];
            arr[i][1]=endTime[i];
            arr[i][2]=profit[i];
        }

        Arrays.sort(arr,(a,b)->a[0]-b[0]);
        return Solve(arr,0,dp);
    }
}

