class Solution {
public:
    bool static comp(pair<pair<int, int>, int>& a, pair<pair<int, int>, int>& b) {  
        return a.first.second < b.first.second;  
    } 
    int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {
        vector<pair<pair<int, int>, int>> vec;
        int n = startTime.size();
        for(int i=0; i<n; i++){
            vec.push_back({{startTime[i], endTime[i]}, profit[i]});
        }
        sort(vec.begin(), vec.end(), comp);
        map<int, int> dp;
        dp.insert({0,0});
        for(int i=0; i<n; i++){
            int end = vec[i].first.second;
            int start = vec[i].first.first;
            int prof = vec[i].second;
            auto prevJobEnd = dp.lower_bound(start);
            int prevjobend = prevJobEnd->first;
            if(prevjobend > start)
                --prevJobEnd;
            else if(prevjobend < start)
                prevJobEnd++;

            int newProfit = prevJobEnd->second + prof;
            int maxProfit = max(newProfit, dp.rbegin()->second);  
            dp[end] = maxProfit; 

        }
        return dp.rbegin()->second;

    }
};

