class Solution {
private:
    int getNextIndex(vector<vector<int>> &jobs, int l, int currEnd){
        int n = jobs.size();
        int r = n-1;

        int res = n+1;

        while(l <= r){
            int mid = l + (r - l) / 2;

            if(jobs[mid][0] >= currEnd){
                res = mid;
                r = mid - 1;
            }
            else{
                l = mid + 1;
            }
        }
        return res;
    }
    int f(int i, vector<vector<int>> &jobs, int n, vector<int> &dp){
        // base case in case of out of bound of the vector
        if(i >= n) return 0;
        // DP Check
        if(dp[i] != -1) return dp[i];
        // This is a crucial Time saving step, because here we are concerned, to get the next valid index where the startTime is more or equal to the end time of the curr Index, this can be done using linear search but is better to do by Binary Search(lon(N))
        int next_index = getNextIndex(jobs, i+1, jobs[i][1]);

        // Make the two recursive calls for taken and not taken
        int notTaken = 0 + f(i+1, jobs, n, dp);
        int Taken = jobs[i][2] + f(next_index, jobs, n, dp);

        // Store in the DP array and returun the answer
        return dp[i] = max(Taken, notTaken);
    }
public:
    int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {
        // This Solution is being implemented using the binary Search
        int n = startTime.size();
        vector<vector<int>> jobs(n, vector<int> (3, 0));

        // This has been so that we could sort it on the basis of the startTime
        // Why? -> This is becaue we need to consder all the tasks at first and the one we must start 
        // from the index which is started first and then go consecutively in forward direction, where the events
        // too are sorted in that fashion so that we could get the first element whose startTime is more than the       EndTime of the curr index, and also it is least possible start time.       
        for(int i=0; i<n; i++){
            jobs[i][0] = startTime[i];
            jobs[i][1] = endTime[i];
            jobs[i][2] = profit[i];
        }
        // Simply sort(reason explained above)
        sort(jobs.begin(), jobs.end());
        vector<int> dp(n+1, -1);
        // Make a recursion call
        return f(0, jobs, n, dp);
    }
};

