class Solution {
public:

    int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {

        int n = startTime.size();
        vector<vector<int>> tmp(n);
        for(int i = 0; i < n; ++i) {
            tmp[i] = {startTime[i], endTime[i], profit[i]};
        }

        sort(tmp.begin(), tmp.end());
        for(int i = 0; i < n; ++i) {
            startTime[i] = tmp[i][0];
            endTime[i] = tmp[i][1];
            profit[i] = tmp[i][2];
        }

        vector<int> dp(n + 1, -1);
        dp.back() = profit.back();

        for(int i = n - 1; i >= 0; --i) {
            int include = profit[i];
            // get next non-overlapping range
            // next start that <= curr end
            auto it = lower_bound(startTime.begin(), startTime.end(), endTime[i]);
            if(it != startTime.end()) {
                int nxt = it - startTime.begin();
                include += dp[nxt];
            }
            int exclude = dp[i + 1];
            dp[i] = max(include, exclude);
        }

        return dp[0];
    }
};

