class Solution {
public:
    int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {
        int n = startTime.size();
        vector<vector<int>> jobs(n);
        for (int i = 0; i < n; ++i) {
            jobs[i] = {endTime[i], startTime[i], profit[i]};
        }
        sort(jobs.begin(), jobs.end());

        vector<int> dp(n);
        dp[0] = jobs[0][2];

        for (int i = 1; i < n; ++i) {
            int l = 0, r = i;
            while (l < r) {
                int mid = l + (r - l) / 2;
                if (jobs[mid][0] <= jobs[i][1]) {
                    l = mid + 1;
                } else {
                    r = mid;
                }
            }
            int currProfit = jobs[i][2];
            if (l > 0) {
                currProfit += dp[l - 1];
            }
            dp[i] = max(dp[i - 1], currProfit);
        }
        return dp[n - 1];
    }
};

typedef struct {
    int start;
    int end;
    int profit;
} Tuple;

int compare (Tuple *x, Tuple *y) {
	/* for ascending, return *x - *y
	   for descending return *y - *x */
    if (x->end == y->end) {
        return y->start - x->start;
    }
    return x->end - y->end;
}

void insertionSort(Tuple **a, int lo, int hi) {
    int i, j;
    Tuple *t;
    for (i = lo+1; i <= hi; ++i) {
        t = a[i];
        j = i-1;
        while (j >= lo && compare(a[j], t) > 0) {
           a[j+1] = a[j];
           --j;
        }
        a[++j] = t;
    }
}

void quickSort(Tuple **a, int lo, int hi) {
    int pi, i, j;
    Tuple *pv, *t;
    if (lo >= hi - 10) {
        insertionSort(a, lo, hi);
        return;
    }
    pi = (lo + hi) / 2;
    pv = a[pi];
    a[pi] = a[hi];
    a[hi] = pv;
    i = lo - 1;
    j = hi;
    do {
        do { i++; } while (compare(a[i], pv) < 0);
        do { j--; } while (compare(a[j], pv) > 0 && j > lo);
        if (i < j) {
            t = a[i];
            a[i] = a[j];
            a[j] = t;
        }
    } while (i < j);
    a[hi] = a[i];
    a[i] = pv;
    quickSort(a, lo, i-1); 
    quickSort(a, i+1, hi);
}

int jobScheduling(int* startTime, int startTimeSize, int* endTime, int endTimeSize, int* profit, int profitSize) {
    int i, lo, mid, hi, n = profitSize;
    Tuple pool[n], *job[n];
    for (i = 0; i < n; ++i) {
        job[i] = &pool[i];
        pool[i].start = startTime[i];
        pool[i].end = endTime[i];
        pool[i].profit = profit[i];
    }
    quickSort(job, 0, n-1);
    for (i = 1; i < n; ++i) {
        lo = -1, hi = i-1;
        while (lo < hi) {
            mid = (lo + hi + 1) >> 1;
            if (job[i]->start < job[mid]->end) {
                hi = mid - 1;
            } else {
                lo = mid;
            }
        }
        if (lo >= 0) {
            job[i]->profit += job[lo]->profit;
        }
        if (job[i]->profit < job[i-1]->profit) {
            job[i] = job[i-1];
        }
    }
    return job[n-1]->profit;
}

class Solution {
public:
    int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {
    int n = startTime.size();
    vector<vector<int>> inp(n, vector<int>(3));

    for (int i = 0; i < n; i++) {
        inp[i][0] = startTime[i];
        inp[i][1] = endTime[i];
        inp[i][2] = profit[i];
    }

    sort(inp.begin(), inp.end(), [](const vector<int>& row1, const vector<int>& row2) -> bool {
        return row1[0] < row2[0];
    });

    vector<int> table(n + 1, 0);

    for (int i = n - 1; i >= 0; i--) {
        auto it = lower_bound(inp.begin() + i + 1, inp.end(), inp[i][1], [](const vector<int>& row, int val) {
            return row[0] < val;
        });

        int notTake = table[i + 1];
        int take = (it != inp.end()) ? table[distance(inp.begin(), it)] : 0;

        table[i] = max(inp[i][2] + take, notTake);
    }

    return table[0];
}

};

#define ll long long
class Solution {
public:
    int jobScheduling(vector<int>& s, vector<int>& e, vector<int>& profit) {
        vector<pair<pair<int,int>,int>> range;
        int n= s.size();
        for(int i=0;i<n;i++){
            range.push_back({{s[i],e[i]},profit[i]});
        }
        auto comp = [&]( const pair<pair<int,int>,int>& a,const pair<pair<int,int>,int>& b ){
            return a.first.first < b.first.first ;
        };
        sort(range.begin(),range.end(), comp);
        vector<ll> dp(n+1);
        auto lowerBound = [&](const int x , int i){
            int low = i+1 , high = n-1;
            while(low<=high){
                int mid = (low+high)/2;
                if( range[mid].first.first >= x) high = mid-1;
                else low = mid+1;
            }
            return low ;
        };
        for(int i=0;i<n;i++){
            int ind = lowerBound(range[i].first.second, i);
            if(ind<=n) dp[ind] = max(dp[ind],dp[i]+range[i].second);
            dp[i+1]= max(dp[i+1],dp[i]);
        }
        return dp[n];
    }
};

class Solution {
public:
    int solve(vector<vector<int>>&jobs,vector<int>& startTime,int index,vector<int>&dp){
        if(index==startTime.size()){
            return 0;
        }
        if(dp[index]!=-1)
            return dp[index];
        int nextIndex=lower_bound(startTime.begin(),startTime.end(),jobs[index][1])-startTime.begin();
        //notTake
        int a=solve(jobs,startTime,index+1,dp);
        //take
        int b=jobs[index][2]+solve(jobs,startTime,nextIndex,dp);
        return dp[index]=max(a,b);
    }
    int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {
        vector<vector<int>>jobs;
        int n=startTime.size();
        vector<int>dp(n+1,-1);
        for(int i=0;i<startTime.size();i++){
            jobs.push_back({startTime[i],endTime[i],profit[i]});
        }
        sort(jobs.begin(),jobs.end());
        for(int i=0;i<jobs.size();i++){
            startTime[i]=jobs[i][0];
        }
        return solve(jobs,startTime,0,dp);
    }
};

class Solution {
public:
    int dp[50001];

    int getMaxProfit(int index, vector<vector<int>>&arr, int targetColumn){
        if(index >= arr.size())
            return 0;

        if(dp[index]!=-1)
            return dp[index];

        int res = 0;

        // to find next index
        auto lowerBoundIter = lower_bound(arr.begin(), arr.end(), arr[index][1], [targetColumn](const vector<int>& a, int value) {
            return a[targetColumn] < value;
        });

        int rowIndex = distance(arr.begin(), lowerBoundIter);

        res = max(arr[index][2] + getMaxProfit(rowIndex, arr, targetColumn), getMaxProfit(index+1, arr, targetColumn));

        return dp[index] = res;
    }

    int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {
        int n = profit.size();
        vector<vector<int>>arr(n);

        for(int i=0;i<n;i++){
            arr[i] = {startTime[i], endTime[i], profit[i]};
        }

        sort(arr.begin(), arr.end());
        memset(dp, -1, sizeof(dp));

        int res = 0;

        for(int i=n-1;i>=0;i--){
            int val = getMaxProfit(i, arr, 0);
            res = max(res, val);
        }

        return res;
    }
};

class Solution {
