        if(dp[i]!=-1)return dp[i];
        int t=0, nt=0;
        int ind=lower_bound(s.begin(), s.end(), v[i].second.first)-s.begin();
        t=v[i].second.second+solve(v, s, ind, prev);
        nt=solve(v, s, i+1, prev);
