class Solution {
public:
    int f(int i, vector<vector<int>>&v, vector<int>&dp){
        if(i>=v.size())return 0;
        if(dp[i]!=-1)return dp[i];
        int tk=0, ntk=0;
        int st=i, en=v.size()-1;
        while(st<=en){
            int mid=st+(en-st)/2;
            if(v[mid][0]>=v[i][1])en=mid-1;
            else st=mid+1;
        }
        tk=v[i][2]+f(st, v, dp);
        ntk=f(i+1, v, dp);
        return dp[i]=max(tk, ntk);
    }
    int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {
        vector<vector<int>>v;
        for(auto i=0; i<startTime.size(); i++){
            v.push_back({startTime[i], endTime[i], profit[i]});
        }
        sort(begin(v), end(v));
        vector<int>dp(startTime.size(), -1);
        return f(0, v, dp);
    }
};

class Solution {
public:
    int jobScheduling(vector<int>& startTime, vector<int>& endTime, vector<int>& profit) {
        int n = startTime.size();
        vector<pair<pair<int, int>, int>> job;
        pair<int, int> dp[n+1];
        job.push_back({{0, 0}, 0});
        for(int i = 0; i < n; i++){
            job.push_back({{endTime[i], startTime[i]}, profit[i]});
        }
        sort(job.begin(), job.end());
        dp[0] = {0, 0};
        for(int i = 1; i <= n; i++){
            if(i > 0) dp[i] = dp[i-1];
            int l = 0, r = i-1, mid;
            while(l < r){
                mid = (l+r+1)/2;
                if(dp[mid].second <= job[i].first.second) l = mid;
                else r = mid-1;
            }
            if(dp[i].first < dp[l].first + job[i].second){
                dp[i] = {dp[l].first + job[i].second, job[i].first.first};
            }
        }
        return dp[n].first;
    }
};

class Solution {
