        for (int i = 0; i < n; i++) {
            v[i] = make_tuple(endTime[i], startTime[i], profit[i]);
        }
        sort(v.begin(), v.end());
        vector<int> dp(n);
        for (int i = 0; i < n; i++) {
            dp[i] = get<2>(v[i]);
            if (i > 0) {
                dp[i] = max(dp[i], dp[i - 1]);
            }
            {
                int l = 0, r = i - 1, at = -1;
                while (l <= r) {
                    int mid = (l + r) >> 1;
                    if (get<0>(v[mid]) <= get<1>(v[i])) {
                        l = mid + 1;
                        at = mid;
                    } else {
                        r = mid - 1;
                    }
                }
                if (at != -1) {
                    dp[i] = max(dp[i], dp[at] + get<2>(v[i]));
                }
            }
        }
        return dp[n - 1];
    }
};

int binarysearcher(vector <vector <int> > &v,int key,int s,int e){
    int temp=v.size();
    while(s<=e){
        int mid=(s+e)/2;
        if (v[mid][0]<key){
            s=mid+1;
        }
        else{
            temp=mid;
            e=mid-1;
        }
    }
    return temp;
}

int helper(int i,vector <vector <int> > &v,vector <int> &dp){
    if (i>=v.size()){
        return 0;
    }
    if (dp[i]!=-1){
        return dp[i];
    }
    //exclude
    int excl=helper(i+1,v,dp);
    //include
    int incl=v[i][2];
    int index=binarysearcher(v,v[i][1],i+1,v.size()-1);//just equal or greater 
    incl+=helper(index,v,dp);
    dp[i]=max(incl,excl);
    return dp[i];
}
