class Solution {
    fun jobScheduling(startTime: IntArray, endTime: IntArray, profit: IntArray): Int {
        var joblist = mutableListOf<Triple<Int, Int, Int>>()
        val n = startTime.size

        for (i in 0 until n) {
            joblist.add(Triple(startTime[i], endTime[i], profit[i]))
        }

        joblist.sortWith(compareBy({ it.second }, { it.first }, { it.third }))

        val tree = TreeMap<Int, Int>()
        tree.put(0, 0)

        var result = 0
        for (job in joblist) {
            val lk = tree.floorKey(job.first)
            val pre = tree.getOrDefault(lk, 0)
            val expt = pre + job.third

            val uk = tree.floorKey(job.second)
            val post = tree.getOrDefault(uk, 0)

            if (post < expt) {
                tree.put(job.second, expt)
                result = maxOf(result, expt)
            }
        }

        return result
    }
}

fun jobScheduling(startTime: IntArray, endTime: IntArray, profit: IntArray): Int {
  val inds = startTime.indices.sortedBy { startTime[it] }
  val dp = IntArray(inds.size + 1)
  for (i in inds.indices.reversed()) {
    var lo = i + 1
    var hi = inds.lastIndex
    while (lo <= hi) {
      val m = lo + (hi - lo) / 2
      if (endTime[inds[i]] > startTime[inds[m]]) lo = m + 1 else hi = m - 1
    }
    dp[i] = max(dp[i + 1], profit[inds[i]] + dp[lo])
  }
  return dp[0]
}

interface Job {
  start: number
  end: number
  profit: number
}

class JobArray extends Array<Job> {
  public maximumProfit (): number {
    const scores = new Array(this.length + 1)
    scores[this.length] = 0
    for (let i = this.length - 1; i >= 0; i--) {
      scores[i] = Math.max(this[i].profit + scores[this.findNextJobIndex(i)], scores[i + 1])
    }
    return scores[0]
  }

  public findNextJobIndex (low: number): number { 
    const target = this[low].end
    let high = this.length
    while (low < high) {
      const mid = (low + high) >>> 1
      if (this[mid].start < target) low = mid + 1
      else high = mid
    }
    return high
  }

  public sortBySchedules (): JobArray {
    return this.sort((a, b) => a.start - b.start || a.end - b.end)
  }

  public static fromInputs (starts: number[], ends: number[], profits: number[]): JobArray {
    const output = new JobArray(starts.length)
    for (let i = 0, bound = starts.length; i < bound; i++) {
      output[i] = { start: starts[i], end: ends[i], profit: profits[i] }
    }
    return output
  }
}

const jobScheduling = (starts: number[], ends: number[], profits: number[]): number => {
  return JobArray.fromInputs(starts, ends, profits).sortBySchedules().maximumProfit()
}

class Solution:
    def jobScheduling(self, startTime: List[int], endTime: List[int], profit: List[int]) -> int:
        n = len(startTime)
        jobs = sorted([(startTime[i], endTime[i], profit[i]) for i in range(n)])

        nextPossJob = [n]*n
        # startorder_i will iterate through jobs in order of startTime (how jobs is ordered)
        # endorder_i will iterate through jobs in order of endTime
        startorder_i = 0
        for endorder_i in sorted(range(n), key=lambda x:jobs[x][1]):
            while startorder_i < n and jobs[endorder_i][1] > jobs[startorder_i][0]:
                startorder_i += 1
            if startorder_i == n:
                break
            nextPossJob[endorder_i] = startorder_i

        dp = [0]*(n+1)
        for i in range(n-1, -1, -1):
            # Two possibilities: use the current job at i or skip to next job
            dp[i] = max(jobs[i][2] + dp[nextPossJob[i]], dp[i+1])
        return dp[0]

class Solution: 
    def jobScheduling(self, startTime: List[int], endTime: List[int], profit: List[int]) -> int:
        q = [0]
        P = [0,0]
        for e,s,p in sorted(zip(endTime, startTime, profit)):
            pp = P[bisect_right(q, s)] + p
            if pp > P[-1]:
                q.append(e)
                P.append(pp)
        return P[-1]

class Solution {
    private static class Job{
        int start, end, profit;

        // Constructor
        Job(int start, int end, int profit){
            this.start = start;
            this.end = end;
            this.profit = profit;
        }
    }

    public int jobScheduling(int[] startTime, int[] endTime, int[] profit) {
        int jobCount = profit.length; // Total number of Jobs
        Job[] jobs = new Job[jobCount];

        for(int index=0; index<jobCount; index++)
            jobs[index] = new Job(startTime[index], endTime[index], profit[index]);

        // Sort the array on basis on EndTime of the Job
        Arrays.sort(jobs, (a,b) -> a.end - b.end);
        int[] dp = new int[jobCount+1];

        for(int index=0; index<jobCount; index++){
            int start = jobs[index].start;
            int end = jobs[index].end;
            int profits = jobs[index].profit;

            int nonConflictIndex = upperBound(jobs, index, start);
            dp[index+1] = Math.max(dp[index], dp[nonConflictIndex] + profits);
        }

        return dp[jobCount];
    }

    // Binary Search
    private int upperBound(Job[] jobs, int endIndex, int targetIndex){
        int low = 0;
        int high = endIndex;

        while(low < high){
            int mid = (low + high) / 2;
            if(jobs[mid].end <= targetIndex)
                low = mid + 1;
            else
                high = mid;
        }

        return low;
    }
}

class Solution {
    func jobScheduling(_ startTime: [Int], _ endTime: [Int], _ profit: [Int]) -> Int {
        let jobs = zip(startTime, zip(endTime, profit)).sorted { $0.1.0 < $1.1.0 }
        var dp: [(time: Int, profit: Int)] = [(0, 0)]

        for job in jobs {
            let currentProfit = dp.last!.profit + job.1.1
            if job.0 >= dp.last!.time {
                dp.append((job.1.0, currentProfit))
            } else {
                var l = 0, r = dp.count - 1
                while l < r {
                    let m = (l + r) / 2
                    if dp[m].time <= job.0 { l = m + 1 }
                    else { r = m }
                }
                let lastProfit = dp[l-1].profit + job.1.1
                if lastProfit > dp.last!.profit {
                    dp.append((job.1.0, lastProfit))
                }
            }
        }

        return dp.last!.profit
    }
}

func jobScheduling(startTime []int, endTime []int, profit []int) int {
    dp := make([]int, len(startTime)+1)
    summary := make([][3]int, len(startTime))
    for i := 0; i < len(startTime); i++ {
        summary[i][0], summary[i][1], summary[i][2] = startTime[i], endTime[i], profit[i]
    }
    sort.Slice(summary, func(i, j int)bool {return summary[i][0] < summary[j][0]})
    sort.Ints(startTime)

    for i := len(startTime)-1; i >= 0; i-- {
        dp[i] = summary[i][2]
        index := sort.SearchInts(startTime, summary[i][1])
        if index < len(startTime) {dp[i] += dp[index]}
        if dp[i] < dp[i+1] {dp[i] = dp[i+1]}
    }
    return dp[0]
}

class Solution {
    public int jobScheduling(int[] startTime, int[] endTime, int[] profit) {
        int len = startTime.length;
        int[][] dp = new int[len][3];
        for(int i=0; i<len; i++){
            dp[i] = new int[]{startTime[i], endTime[i], profit[i]};
        }

        Arrays.sort(dp, (a,b)->a[1]-b[1]);
        TreeMap<Integer, Integer> map = new TreeMap<>();
        map.put(0, 0);
        for(var i:dp){
            int cur = map.floorEntry(i[0]).getValue() + i[2];
            if(cur > map.lastEntry().getValue()){
                map.put(i[1], cur);
            }
        }

        return map.lastEntry().getValue();
    }
}

import "sort"

type BIT struct {
  bit map[int]int
  max int
}

func BIT_init(max_val int) BIT {
  return BIT{
    bit: map[int]int{},
    max: max_val,
  }
}

func (self *BIT) update_max(p, v int) {
  p++
  for p < self.max {
    if v > self.bit[p] {
      self.bit[p] = v
    }
    p += p & (-p)
  }
}

func (self *BIT) prefix_max(p int) int {
  res, p := 0, p + 1

  for p != 0 {
    if self.bit[p] > res {
      res = self.bit[p]
    }
    p -= p & (-p)
  }
  return res
}

func get_data(ts_start []int, ts_end []int, profit []int) ([][3]int, int) {
  n, max_val := len(ts_start), 0
  data := make([][3]int, n)

  for i := 0; i < n; i++ {
    data[i] = [3]int{ts_start[i], ts_end[i], profit[i]}
    if ts_end[i] > max_val {
      max_val = ts_end[i]
    } 
  }

  sort.Slice(data, func(i, j int) bool { return data[i][1] < data[j][1]})
  return data, max_val + 2
}

func jobScheduling(ts_start []int, ts_end []int, profit []int) int {
  data, max_val := get_data(ts_start, ts_end, profit)

  bit := BIT_init(max_val)
  for _, v := range data {
    bit.update_max(v[1], bit.prefix_max(v[0]) + v[2])
  }

  return bit.prefix_max(max_val - 2)
}

// Job struct represents a job with startTime, endTime, and profit
type Job struct {
	startTime, endTime, profit int
}

// ByEndTime implements sort.Interface based on the endTime field
type ByEndTime []Job

func (a ByEndTime) Len() int           { return len(a) }
func (a ByEndTime) Swap(i, j int)      { a[i], a[j] = a[j], a[i] }
func (a ByEndTime) Less(i, j int) bool { return a[i].endTime < a[j].endTime }

// binarySearch finds the latest job that doesn't overlap with the current job
func binarySearch(jobs []Job, index int) int {
	low, high := 0, index-1
	for low <= high {
		mid := (low + high) / 2
		if jobs[mid].endTime <= jobs[index].startTime {
			if jobs[mid+1].endTime <= jobs[index].startTime {
				low = mid + 1
			} else {
				return mid
			}
		} else {
			high = mid - 1
		}
	}
	return -1
}

// maxProfitJobSchedulingGreedy calculates the maximum profit for non-overlapping jobs using greedy algorithm
func jobScheduling(startTime []int, endTime []int, profit []int) int {
	n := len(startTime)
	jobs := make([]Job, n)

	for i := 0; i < n; i++ {
		jobs[i] = Job{startTime[i], endTime[i], profit[i]}
	}

	// Sort jobs based on endTime
	sort.Sort(ByEndTime(jobs))

	// dp[i] stores the maximum profit till job i
	dp := make([]int, n)
	dp[0] = jobs[0].profit

	for i := 1; i < n; i++ {
		// Calculate profit including the current job
		including := jobs[i].profit
		l := binarySearch(jobs, i)
		if l != -1 {
			including += dp[l]
		}

		// Calculate profit excluding the current job
		excluding := dp[i-1]

		// Store the maximum profit till job i
		dp[i] = max(including, excluding)
	}

	return dp[n-1]
}

// max returns the maximum of two integers
func max(a, b int) int {
	if a > b {
		return a
	}
	return b
}

func jobScheduling(startTime []int, endTime []int, profit []int) int {
    end2Detail := make(map[int][][]int)
    minEnd, maxEnd := 1000000001, -1
    for i:=0; i<len(startTime); i++ {
        minEnd=min(minEnd, endTime[i])
        maxEnd=max(maxEnd, endTime[i])

        end2Detail[endTime[i]]=append(end2Detail[endTime[i]], []int{startTime[i], profit[i]})
    }

    sort.Ints(endTime)
    dp := make([][]int, 0) //[[endTime, maxProfit], [endTime, maxProfit], [endTime, maxProfit]..]
    maxTillNow:=-1
    for _, e := range endTime {
        tempDp:=[]int{e, 0}
        for _, v := range end2Detail[e] {
            tempDp[1]=max(tempDp[1], v[1]+maxTill(dp, v[0]))
        }
        tempDp[1]=max(tempDp[1], maxTillNow)
        maxTillNow=tempDp[1]
        dp=append(dp, tempDp)

    }

    return maxTillNow
}

func maxTill(dp [][]int, start int) int {
    if len(dp)==0 {
        return 0
    }

    // return profit for endTime just less than provided startTime
    l, r:=0, len(dp)-1
    res:=0
    for l<=r {
        m:=(l+r)/2
        if dp[m][0] == start {
            res=dp[m][1]
            break
        }
        if dp[m][0] < start {
            res=dp[m][1]
            l=m+1
        } else {
            r=m-1
        }
    }
    return res
}

