def job_scheduling(start_time, end_time, profit)
    se    = end_time.sort
    end_time.each_index.to_a.sort_by! {|i| 
        end_time[i] 
    }.each_with_index.each_with_object(Array.new(end_time.size.succ, 0)) {|(idx, i), dp|
        dp[i+1] = dp[i].max(profit[idx] + dp[se.bsearch_index {|w| w > start_time[idx]} || -1])
    }.last
end

from typing import List
import bisect

class Solution:
    def jobScheduling(self, startTime: List[int], endTime: List[int], profit: List[int]) -> int:
        jobs = sorted(zip(endTime, startTime, profit))
        dp = [(0, 0)]  # (endTime, profit)

        for end, start, profit in jobs:
            # Find the last job that does not conflict
            i = bisect.bisect_right(dp, (start, float('inf'))) - 1
            if dp[i][1] + profit > dp[-1][1]:
                dp.append((end, dp[i][1] + profit))

        return dp[-1][1]
